var promise = BoFactory.loadObjectByParamsAsync(LO_ORDERNOTES, me.getQueryBy("sdoMainPKey", me.getPKey())).then(
  function (loNotes) {
    me.setLoNotes(loNotes);
    var items = loNotes.getItemObjects();
    for (var i = 0; i < items.length; i++) {
      items[0].setObjectStatus(STATE.PERSISTED);
    }
  });
