BusinessLogic.Code:
var validate = false;
var deferreds = [];
me.getLoItems().suspendListRefresh();

if(Utils.isDefined(itemMeta)){
  if(me.getBoOrderMeta().getConsiderItemModReason() == "1" &&
     itemMeta.getModReasonRequired() == "Mand" &&
     mainItem.getSuggestedQuantity() !== 0 &&
     mainItem.getModReason() == " "){
    if(mainItem.getSuggestedQuantity() == mainItem.getQuantity()){
      deferreds.push(mainItem.setModReasonEntered('EmptyImage'));
    }
    else{
      validate = true;
      deferreds.push(mainItem.setModReasonEntered('PrioHigh24'));
    }
  }
  else{
    deferreds.push(mainItem.setModReasonEntered('EmptyImage'));
  }
}
else{
  deferreds.push(mainItem.setModReasonEntered('EmptyImage'));
}

var promise = when.all(deferreds).then(
  function() {
    var tempPromise;
    //check main Item Icon
    if(checkParent == "1"){
      var itemsWithIconExist = me.getLoItems().containsItem({"modReasonEntered": 'PrioHigh24', "refPKey": mainItem.getRefPKey() },
                                                            [{ "fieldName": "modReasonEntered" }, { "fieldName": "refPKey" }]);

      if(itemsWithIconExist){
        tempPromise = mainItem.setModReasonEntered('PrioHigh24');
      }
      else{
        tempPromise = mainItem.setModReasonEntered('EmptyImage');
      }
    }
    me.getLoItems().resumeListRefresh(true);
    return when(tempPromise).then(function() {
      return validate;
    });
  });
