var currentSplittingGroups = [];
if (!Utils.isDefined(availableSplittingGroups)){
  availableSplittingGroups = me.getLoSplittingGroups().getAllItems();
}

// Filter the current splitting group.
for(var i=0; i < availableSplittingGroups.length; i++){
  if(availableSplittingGroups[i].splittingGroup != splittingGroup){
    for(var j=0; j < availableSplittingGroups.length-1; j++){
      currentSplittingGroups[j] = availableSplittingGroups[i];
    }
  }
}

if (currentSplittingGroups.length === 0){
  currentSplittingGroups = "Empty";
}
