BusinessLogic.Code:
var params = { "filterCode": "Basket" };
var itemFilter = me.getLoItemFilter();

if (Utils.isDefined(itemFilter)){
  var filter =  itemFilter.getItemsByParam(params);
  if (filter.length > 0) {
    var data = filter[0].getCount();
    var dataItems = data.toString().split("/");
    filter[0].setCount(dataItems[0] + "/" + me.getTotalValue().toFixed(2));
  }
}
