BusinessLogic.Code:
if(!Utils.isEmptyString(selectedPromotionPKey) && me.rewardGroupExistsInOrder()) {
  var selectablePromotion = me.getLoSelectablePromotion().getItemByPKey(selectedPromotionPKey);
  if(Utils.isDefined(selectablePromotion)){
    var rewardGroupSatisfactionCounter = "Satisfied Reward Groups: " + selectablePromotion.getSatisfiedRewardGroupCount() + "/" + selectablePromotion.getTotalRewardGroup();
    me.getHurdleEvaluationHelper().setRewardsAndHurdlesTitle(rewardGroupSatisfactionCounter);
    me.getHurdleEvaluationHelper().setRewardsButtonCounter(selectablePromotion.getSatisfiedRewardGroupCount() + "/" + selectablePromotion.getTotalRewardGroup());
  }
}
else {
  me.getHurdleEvaluationHelper().setRewardsAndHurdlesTitle(me.getLuOrderer().getName());
}
