BusinessLogic.Code:
var criterionAttribute;
var mappingItems = this.getLoPrdGroupLevelCriterionMapping().getItemsByParam({ "groupLevel": this.getFlatItemListGrouping() });

if (mappingItems.length > 0) 
{
  criterionAttribute = mappingItems[0].getCriterion().toLowerCase();
} 
else 
{
  //Fallback
  if(Utils.isSfBackend())
  {
    criterionAttribute = "Criterion_3_Product_Code__c";              
  }
  else
  {
    criterionAttribute = "criterion3";
  }
}
