BusinessLogic.Code:

var count = me.cardItemCount;
var numberVisibleOrders = 5;

if(Utils.isPhone()) {
	numberVisibleOrders = 3;
}

if(count <= numberVisibleOrders){
  numberVisibleOrders = count;
}

var info = " ";
if(numberVisibleOrders != "0"){
	info = numberVisibleOrders.toString() + " / " + count;
}
