BusinessLogic.Code:
//Add selected quick filter item from QuickFilterCache to LoProductCategoryFilter on tab change
var quickFilterCache = bo.getCategoryQuickFilterCache();

if(Utils.isDefined(quickFilterCache) && !Utils.isEmptyString(quickFilterCache) && bo.getLoProductCategoryFilter().getItemsByParam({"text": quickFilterCache}).length === 0){
  var liProductCategoryFilter =  {
    "pKey": PKey.next(),
    "text": quickFilterCache,
    "isSelected": true,
    "specialOption": "",
    "filter": "Category"
  };
  bo.getLoProductCategoryFilter().addListItems([liProductCategoryFilter]);
}
