BusinessLogic.Code:
//Add selected quick filter item from QuickFilterCache to LoProductQuickFilter on tab change
var quickFilterCache = bo.getGroupQuickFilterCache();

if(Utils.isDefined(quickFilterCache) && !Utils.isEmptyString(quickFilterCache) && bo.getLoProductQuickFilter().getItemsByParam({"text": quickFilterCache}).length === 0){
  var liProductQuickFilter =  {
    "pKey": PKey.next(),
    "text": quickFilterCache,
    "isSelected": true,
    "specialOption": "",
    "filter": "Group"
  };
  bo.getLoProductQuickFilter().addListItems([liProductQuickFilter]);
}
