BusinessLogic.Code:
var scannedProductResult;

var items = me.getItemObjects();

switch (items.length) 
{                           
  case 0:
    scannedProductResult = "NOPRODUCTS";
    break;
  case 1:
    scannedProductResult = "ONEPRODUCT";
    break;
  default :
    scannedProductResult = "MULTIPLEPRODUCTS";
    break;
}
