BusinessLogic.Code:
var boAnnotation = me.getBoAnnotation();
var loRecentState = me.getLoRecentState();
var promises = [];


promises.push(Facade.saveObjectAsync(me));

if(Utils.isDefined(boAnnotation)) {
	promises.push(boAnnotation.saveAsync());
}
if(Utils.isDefined(loRecentState)) {
	promises.push(loRecentState.saveAsync());
}

var promise = when.all(promises)
.then(
  function () {
    return me;
  });
