var newParams  = context.jsonQuery;

if (Utils.isOldParamsFormat(context.jsonQuery))
{
  newParams = Utils.convertDsParamsOldToNew(context.jsonQuery);
  context.jsonQuery = newParams;
} 

var cancelIsEnabled = "0";
var deleteIsEnabled = "0";

var currentTourStatus = ApplicationContext.get('currentTourStatus');

if(Utils.isDefined(currentTourStatus) && currentTourStatus.toLowerCase() === "running" && newParams.phase === "Initial" && newParams.mobilityRelevant == "1") {
    cancelIsEnabled = "1";

    if(newParams.initiatorPKey === ApplicationContext.get('user').getPKey()) {
      deleteIsEnabled = "1";
    }
}  

me.addItems([
  {
    "id" : "0000001",
    "actionImg" : "CancelDarkGrey24",
    "actionId" : "Cancel",
    "processEvent" : "Cancel",
    "actionVisible" : "1",
    "actionEnabled" : cancelIsEnabled
  },
  { 
    "id" : "0000002",
    "actionImg" : "TrashcanDarkGrey24",
    "actionId" : "Delete",
    "processEvent" : "Delete",
    "actionVisible" : "1",
    "actionEnabled" : deleteIsEnabled
  }
]);

