var promise;
if(!Utils.isDefined(me.getLoContact())) {
  promise = BoFactory.loadListAsync(LO_CONTACT, me.getQueryBy("accountId", me.getAccountId())).then(
    function (loContactsJson) {
      var objectState = me.getObjectStatus();
      me.setLoContact(loContactsJson);
      me.setObjectStatus(objectState);
    });
}
else{
  promise = when.resolve();
}
