var promise=when.resolve(result);

if (Utils.isSfBackend()) {
  var promotionList = me.getAllItems();

  for(var i = 0; i < promotionList.length; i++) {  
    var dateFrom = Localization.localize(promotionList[i].dateFrom, "date"); 
    var dateThru = Localization.localize(promotionList[i].dateThru, "date"); 
    promotionList[i].dateText = dateFrom + " - " + dateThru;
  }
}
