BusinessLogic.Code:
var promise = Facade.getListAsync("LoSalesFolderPromotion", context.jsonQuery)
.then(function(salesFolderPromotions) {		

  if(!Utils.isSfBackend()){
    for(var i = 0; i < salesFolderPromotions.length; i++) {
      salesFolderPromotions[i].dateFromText = Localization.localize(salesFolderPromotions[i].dateFrom, "date");
      salesFolderPromotions[i].dateThruText = Localization.localize(salesFolderPromotions[i].dateThru, "date");
    }
  }

  me.addItems(salesFolderPromotions, context.jsonQuery.params);
});
