BusinessLogic.Code:
var WizardHeaderLabel;

if ( MetaType === "Issue" )
{
  WizardHeaderLabel = Localization.resolve("SvcRequestMetaLookupIssueId");
  if(!Utils.isDefined(WizardHeaderLabel))
  {
    WizardHeaderLabel = "Customer Task Types";
  }

  if( IssueType === "ToDo")
  {
    WizardHeaderLabel = "Task Types";
  }
}
else if( MetaType === "Todo")
{
  WizardHeaderLabel = Localization.resolve("SvcRequestMetaLookupTodoId");
  if(!Utils.isDefined(WizardHeaderLabel))
  {
    WizardHeaderLabel = "Task Types";
  }
}
