var steps = me.getLoSysReleaseProcessStep();
var length = steps.getAllItems().length;
var visible = false;

switch(groupName) {
  case "Group1":
    visible = length > 0;
    break;

  case "Group2":
    visible = length > 1;
    break;

  case "Group3":
    visible = length > 2;
    break;

  case "Group4":
    visible = length > 3;
    break;
}
