BusinessLogic.Code:
var stepItems = me.getLoSysReleaseProcessStep().getAllItems();
var length = stepItems.length;

for(var i = 1; i <= length; i++) {
  me.set("signatureGroupName" + i, stepItems[i-1].getText());
  //Remove space from text
  me.set("signatureId" + i, "");
  me.set("signatureCode" + i, "");
}
