BusinessLogic.Code:
var deferreds = [];
var promise = when.resolve();

if(me.getReleaseInProgress() == "1") {
  // Validate name required and signature required for signature steps
  var stepItems = me.getLoSysReleaseProcessStep().getAllItems();
  for (var i = 1; i <= stepItems.length; i++) {
    deferreds.push(me.validateStepAuthentication(messageCollector, stepItems[i-1], i));        
  }
  promise = when.all(deferreds);
}
