BusinessLogic.Code:
var count = me.cardItemCount;
var numberVisibleMobileLinks = 5;
if(Utils.isPhone()){
  numberVisibleMobileLinks = 3;
}

if(count <= numberVisibleMobileLinks){
  numberVisibleMobileLinks = count;
}
var info = " ";
if(numberVisibleMobileLinks != "0"){
  info = numberVisibleMobileLinks.toString() + " / " + count;
}
