BusinessLogic.Code:
// Validate if Due Date is blank
if (Utils.isDefined(me.getDueDate())){
  var dueDate = me.getDueDate();

  if((dueDate != Utils.getMinDateAnsi()) && (dueDate < me.getInitiationDate()) &&  me.getObjectStatus() !== (STATE.DELETED | STATE.NEW | STATE.DIRTY)) {
    messageCollector.add({
      "level": "error",
      "objectClass": "BoTodo",
      "messageID": "CasBoToDoInitiationDateGTDueDate"
    });
  }
}
else{
  me.setDueDate(Utils.getMinDateAnsi());
}
