var promise = Facade.getListAsync("LoTeamUserOverview", Utils.convertDsParamsNewToOld({ "teamList" : [ApplicationContext.get('user').getPKey()]} )).then(
  function (teamLookup) {
    if(Utils.isDefined(teamLookup) && teamLookup.length > 0 && Utils.isDefined(teamLookup[0].userAccountableId)){
      return teamLookup[0].userAccountableId;
    }
  }
);
