BusinessLogic.Code:

 var me = this;
 var acl = me.getACL();
 if (me.getLuSvcRequestMeta().getIsPrivate() == '0') {
     me.getLuUser().getACL().addRight(AclObjectType.PROPERTY, "name", AclPermission.VISIBLE);
}else {                    
     me.getLuUser().getACL().removeRight(AclObjectType.PROPERTY, "name", AclPermission.VISIBLE);
 }
