BusinessLogic.Code:
var initiationDate = Utils.createAnsiDateTimeToday();

// Validate if Due Date is blank
if (Utils.isDefined(me.getDueDate())) {
  if (
    me.getDueDate() != Utils.getMinDate() &&
    me.getDueDate() < initiationDate
  ) {
    messageCollector.add({
      level: "error",
      objectClass: "BoWizardCreateNewTodo",
      messageID: "CasBoToDoInitiationDateGTDueDate",
    });
  }
} else {
  me.setDueDate(Utils.getMinDate());
}
