BusinessLogic.Code:
var orderUnitItem = mainItem.getIsOrderUnit() == "1" ? mainItem : undefined;

if(!Utils.isDefined(orderUnitItem)){
  var items = me.getItemsByParamArray([{
    "refPKey" : mainItem.getRefPKey(),
    "isOrderUnit": "1"
  }]);

  if (items.length > 0){
    orderUnitItem = items[0];
  }
}
