BusinessLogic.Code:
var liTruckLoadItem = me.getCurrent();
var acl = liTruckLoadItem.getACL();

if (me.isEditable()) {
  if (liTruckLoadItem.getTargetQuantity() === 0) {
    acl.addRight(AclObjectType.PROPERTY, "quantityLogisticUnit", AclPermission.EDIT);
  } else {
    acl.removeRight(AclObjectType.PROPERTY, "quantityLogisticUnit", AclPermission.EDIT);
  }
  if (Utils.isDefined(documentType) && (documentType === "ProductCheckIn" || documentType === "TruckAudit") &&
      liTruckLoadItem.getTargetQuantity() === 0) {
    acl.addRight(AclObjectType.PROPERTY, "sdoItemMetaPKey", AclPermission.EDIT);
  }else {
    acl.removeRight(AclObjectType.PROPERTY, "sdoItemMetaPKey", AclPermission.EDIT);
  }
}

if(Utils.isDefined(computePrice) && computePrice !== "4" && computePrice !== "5"){
  acl.removeRight(AclObjectType.PROPERTY, "value", AclPermission.VISIBLE);
  acl.removeRight(AclObjectType.PROPERTY, "valueDifference", AclPermission.VISIBLE);
  acl.removeRight(AclObjectType.PROPERTY, "targetValue", AclPermission.VISIBLE);
}

BindingUtils.refreshEARights();
