BusinessLogic.Code:
var items = me.getAllItems();
var listACL = me.getACL();
me.orderBy({"prdId" : "ASC"});

for (var i = 0; i < items.length; i++){
  var item = items[i];
  item.setTargetValue(item.getValue());
  item.setValueDifference(0);
  if((item.getShortText() === "Prd Out") && Utils.isPhone()){
    item.setShortText("");
  }
}

listACL.removeRight(AclObjectType.PROPERTY, "shortText", AclPermission.VISIBLE);
