BusinessLogic.Code:
var jsonQuerySysSetting = {};
jsonQuerySysSetting.iD = "ClbGoogleMapProviderLicense";

var promise = BoFactory.loadObjectByParamsAsync("LuSysSettingById", jsonQuerySysSetting).then(
  function(luSysSettingById){
    var value = luSysSettingById.getValue();
    ApplicationContext.setOnGroup("mapSettings", "client", value);
    if(!Utils.isEmptyString(value)) {
      ApplicationContext.setOnGroup("mapSettings", "key", value);        
    }
  }
);
