BusinessLogic.Code:
var promise = when.resolve(result);
var aclBoUserSettings = me.getACL();

if (me.getSyncTimeIntervall() > 0)
{
  //greater than zero value so enable checkbox.
  me.setEnableSync(1);
  aclBoUserSettings.addRight(AclObjectType.PROPERTY, "syncTimeIntervall", AclPermission.VISIBLE);
}
else
{
  //else disable checkbox.
  me.setEnableSync(0);
  aclBoUserSettings.removeRight(AclObjectType.PROPERTY, "syncTimeIntervall", AclPermission.VISIBLE);
}
