BusinessLogic.Code:
var syncTimeIntervall = me.getSyncTimeIntervall() * 60;
Facade.startEndlessReplication(syncTimeIntervall, undefined);

var tglQuery = {};

tglQuery.params = [
  { "field" : "id", "value" : "RpsPictureQuality" },
  { "field" : "code", "value" : me.getPictureQuality() }
];

var promise = BoFactory.loadObjectByParamsAsync("LuSysToggleItem", tglQuery).then(
  function (lusystoggle) {
    me.setPictureQualityValues(lusystoggle.getValidationCode());
    me.setObjectStatus(me.self.STATE_UNMODIFIED);
  }
);
