// Set color of axis line
d3.select(".c3-axis-x").select("path").style("stroke","#3D424D");
d3.select(".c3-axis-y").select("path").style("stroke","#3D424D");

// Set color of tick lines
d3.select(".c3-axis-x").selectAll("line").style("stroke","#3D424D");
d3.select(".c3-axis-y").selectAll("line").style("stroke","#3D424D");

// Set color of axis labels
d3.select(".c3-axis-x").selectAll("text").style("fill","#3D424D");
d3.select(".c3-axis-y").selectAll("text").style("fill","#3D424D");
