var backgroundRect = d3.select(chartSelector + "_chartBackground")[0][0];

if (!Utils.isDefined(backgroundRect)) {
   d3.select(chartSelector).select("svg").select("g").insert("rect", "rect").attr("id", chartSelector.substring(1, chartSelector.length) + "_chartBackground").attr("width", width).attr("height", "100%").attr("fill", "#ffffff");
} else {
   d3.select(chartSelector + "_chartBackground").attr("width", width).attr("height", "100%").attr("fill", "#ffffff");
}
