BusinessLogic.Code:
// returns Date Difference in Minutes
//Input is UTC dates -> TimeShiftings will not be captured

var dateDiff = 0;

var dateFrom = this.getUTCDateWithoutSeconds(ansiDateFrom);
var dateThru = this.getUTCDateWithoutSeconds(ansiDateThru);

dateDiff = (dateThru.getTime() - dateFrom.getTime())/1000/60;
