BusinessLogic.Code:
//example:   121 --> 02:01
//           -1  --> 00:00

var formattedTimeString = "00:00";

if(Utils.isDefined(totalDurationInMinutes) && totalDurationInMinutes > 0){
    var durationHours = Math.floor(totalDurationInMinutes/60) > 9 ?  Math.floor(totalDurationInMinutes/60) : "0" + Math.floor(totalDurationInMinutes/60);
    var durationMinutes = totalDurationInMinutes%60 > 9 ? totalDurationInMinutes%60 : "0" + totalDurationInMinutes%60;
    formattedTimeString = durationHours + ":" + durationMinutes;
}
