BusinessLogic.Code:
var guids = [];

if (Utils.isSfBackend()) {
  if (!Utils.isDefined(numberOfGuids)) {
    numberOfGuids = 1;
  }

  var sfUserID = ApplicationContext.get('user').getPKey();

  var timestamp = Date.now();

  for (var i = 1; i <= numberOfGuids; i++) {
    guids.push(sfUserID + timestamp + ('000' + i).substr(-4));
  }
}
