BusinessLogic.Code:
var visible = false;

switch (cardName){

  case "CardUserWelcome": 
    visible = true;
    break;

  case "CardSync": 
    visible = true;
    break;

  case "CardVisits": 
    visible = true;
    break;

  case "CardReporting": 
    visible = true;
    break;

  case "CardUserNotifications": 
    visible = true;
    break;

  case "CardTasks": 
    if(!ApplicationContext.get('user').hasRole('TourUser')){
      visible = true;
    }
    break;

  case "CardCustomerTasks": 
    if(!ApplicationContext.get('user').hasRole('TourUser')){
      visible = true;
    }
    break;

  case "CardUserVisitsGuide":
    visible = true;
    break;

  case "CardActivities":
    visible = true;
    break;

  case "CardMobileLinks":
  case "CardQuickAccess":
    if(Utils.isSfBackend()){
      visible = true;
    }
    break;

  case "CardCustomersOverdue":
    visible = true;
    break;
}
