BusinessLogic.Code:
var visible = false;

switch (cardName) {

  case 'CardHeader':
  case 'CardReportingSurveyExceptions':
  case 'CardActivities':
  case 'CardCustomerTasks':
  case 'CardPicture':
  case 'CardCallNotes':
  case 'CardActivityPerformance':
  case 'CardAccountReceivables':
  case 'VisitTasks':
  case 'VisitDetails':
  case 'RetailStoreCockpit_CardOrderHistory':
    visible = true;
    break;

  case 'CardFollowUpVisits':
    visible = Utils.isSfBackend() && (me.mobilityUserGuidance === 'Merchandizing' || me.mobilityUserGuidance === 'Sales');
    break;

  case 'CardSellIns':
  case 'CardOrderHistory':
    visible = me.mobilityUserGuidance == 'Sales';
    break;

  case 'CardMobileLinks':
  case 'CardQuickAccess':
    visible = Utils.isSfBackend();
    break;
}
