  promise = MessageBox.displayMessage(Localization.resolve("MessageBox_Title_Validation"), Localization.resolve("ProductScan_NoValidStoreLocationText"), buttonValues) 
    .then(function (input) {
    return validInstoreProduct;						
  });
} else {
  promise = when.resolve(validInstoreProduct);
}
