BusinessLogic.Code:
var promise;
var rvkpiList = me.getLoRetailVisitKPI().getAllItems();
var validStoreProduct = false;
if(Utils.isDefined(productId) && !Utils.isEmptyString(productId) && rvkpiList.length > 0){
  for(var i=0 ; i<rvkpiList.length; i++){
    if(rvkpiList[i].productId === productId){
      validStoreProduct = true;
      promise = when.resolve(validStoreProduct);
      break;
    }
  }
  if(!validStoreProduct){
    var buttonValues = {};
    buttonValues[Localization.resolve("OK")] = "ok";
    promise = MessageBox.displayMessage(Localization.resolve("MessageBox_Title_Validation"), Localization.resolve("ProductScan_NoValidStoreText"), buttonValues) 
      .then(function (input) {
      return validStoreProduct;						
    });
  }
}
