var contextMenuItemList = [];

var enabledForCurrentKPI = '1';

if(Utils.isDefined(context.jsonQuery[0]) && Utils.isDefined(context.jsonQuery[0].pKey) && context.jsonQuery[0].pKey.toLowerCase().startsWith("local_")){
   enabledForCurrentKPI = '0';
}

var menuEntry0 = {
  "id" : "0000001",
  "actionImg" : "MenuCapturePicture",
  "actionId" : "AddImage",
  "processEvent" : "AddImage",
  "actionVisible" : "1",
  "actionEnabled" : enabledForCurrentKPI
};

var menuEntry1 = {
  "id" : "0000002",
  "actionImg" : "MenuSelectPicture",
  "actionId" : "ReviewImages",
  "processEvent" : "ReviewImages",
  "actionVisible" : "1",
  "actionEnabled" : enabledForCurrentKPI
};


contextMenuItemList.push(menuEntry0);
contextMenuItemList.push(menuEntry1);
me.addItems(contextMenuItemList);


var promise = when.resolve(me);
