BusinessLogic.Code:
var promise;
var maxNoOfPictures = 5;
var currentImages = me.getAllItems();
var currentImageCount = me.getAllItems().length;
var imageCountExceeded = false;    
//Check the count of pictures exceeding the max count
if (currentImageCount >= maxNoOfPictures) {
  imageCountExceeded = true;
  var buttonValues = {};
  buttonValues[Localization.resolve("OK")] = "ok";
  promise = MessageBox.displayMessage(Localization.resolve("MessageBox_Title_Validation"), Localization.resolve("NoOfPicturesExceedsMaxCount"), buttonValues) 
    .then(function (input) {
    return imageCountExceeded;						
  });
} else {
  promise = when.resolve(imageCountExceeded);
}
