var items = result.getAllItems();

var keys = [];

var maxLimit = 5;

for(var index = maxLimit; index < items.length; index++) {
  keys.push(items[index].pKey);
} 

for(var index = 0; index < keys.length; index++) {
   result.removeItemByPKey(keys[index]);
}

var promise=when.resolve(result);
