BusinessLogic.Code:
var items = me.getAllItems();
me.suspendListRefresh();
for(var i=0; i < items.length; i++ ) {
  items[i].setPlannedStartDateTime(items[i].getPlannedStartDate()+" "+items[i].getPlannedStartTime()+":00");
  items[i].setPlannedEndDateTime(items[i].getPlannedEndDate()+" "+items[i].getPlannedEndTime()+":00");
}
me.resumeListRefresh(true);
var promise=when.resolve(context);
