BusinessLogic.Code:
if (Utils.isDefined(productId)){

  me.resetAllFilters();

  var filterArray = [];
  filterArray.push( {"productId" : productId, "op" : "EQ"});

  if(Utils.isDefined(boAssessmentTask)){
    if(Utils.isDefined(boAssessmentTask.getCategoryFilter()) && !Utils.isEmptyString(boAssessmentTask.getCategoryFilter())){
      filterArray.push({"productCategoryName": boAssessmentTask.getCategoryFilter() , "op": "EQ"});
    } 

    if(Utils.isDefined(boAssessmentTask.getLocationFilter()) && !Utils.isEmptyString(boAssessmentTask.getLocationFilter())){
      filterArray.push({"inStoreLocationName": boAssessmentTask.getLocationFilter() , "op": "EQ"});
    } 

  }
  me.setFilterArray(filterArray);
}
