var info = "";
var totalVisits = me.getAllItemsCount();
var numberOfVisibleVisits = limitOfCardItems;
if(totalVisits <= numberOfVisibleVisits)
  {
    numberOfVisibleVisits = totalVisits;
  }
if(numberOfVisibleVisits != "0")
  {
    info = numberOfVisibleVisits.toString() + " / " + totalVisits;
  }
