me.updateProperties(jsonQuery);

var promise = BoFactory.loadListAsync(LO_WFESTATE, me.getQueryBy("wfeWorkflowPKey", me.getPKey())).then(
  function (object) {
    me.setLoWfeState(object);

    return BoFactory.loadListAsync(LO_WFESTATETRANSITIONS,  me.getQueryBy("wfeWorkflowPKey", me.getPKey()));
  }).then(
  function (object) {
    me.setLoWfeStateTransitions(object);
    me.setObjectStatus(this.self.STATE_NEW);

    return me;
  }
);
