BusinessLogic.Code:
var promise = Facade.getObjectAsync(BO_WORKFLOW, jsonQuery).then(
  function (selfJson) {      
    me.setProperties(selfJson);

    return BoFactory.loadListAsync(LO_WFESTATE, me.getQueryBy("wfeWorkflowPKey", me.getPKey()));
  }).then(
  function (object) {
    me.setLoWfeState(object);

    return BoFactory.loadListAsync(LO_WFESTATETRANSITIONS,  me.getQueryBy("wfeWorkflowPKey", me.getPKey()));
  }).then(
  function (object) {
    me.setLoWfeStateTransitions(object);
    me.setObjectStatus(STATE.PERSISTED);

    return me;
  }
);
